__NUXT_JSONP__("/museum", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o){return {data:[{contents:[{id:"yjj0mwc8z",createdAt:b,updatedAt:b,publishedAt:b,revisedAt:b,eyecatch:{url:f,height:g,width:a},title:"F公園",area:"tochigi",category:[h],text:"\u003Cp\u003Eアスレチックとして有名なF公園。\u003Cbr\u003Eエリアの1つに「恐竜エリア」があり、恐竜のレプリカがそのまま遊具になっているという遊び心あふれる公園。\u003Cbr\u003E\u003Cbr\u003E\u003C\u002Fp\u003E"},{id:"8-wp6mlec",createdAt:c,updatedAt:c,publishedAt:c,revisedAt:c,eyecatch:{url:i,height:j,width:a},title:"E博物館",area:"ishikawa",category:[k],text:"\u003Cp\u003Eあの人気アニメ〇〇の舞台となったことでも知られるE博物館。\u003Cbr\u003E\u003Cbr\u003EIT化が進んでおり、展示された恐竜の音声案内はもちろん、VRによる恐竜時代体験もできるのが特徴的。\u003C\u002Fp\u003E"},{id:"ts_pby941",createdAt:d,updatedAt:d,publishedAt:d,revisedAt:d,eyecatch:{url:f,height:g,width:a},title:"D公園",area:"chiba",category:[h],text:"\u003Cp\u003E入り口の巨大恐竜レプリカが特徴の公園。\u003Cbr\u003E\u003Cbr\u003E雨の日は雨をしのげる場所が少ないので、天気の良い日に訪れることをお勧めします。\u003Cbr\u003Eトイレには男女兼用のオムツ替えシートがあり、小さい子連れのファミリーでも安心の公園です。\u003C\u002Fp\u003E"},{id:"up0mfk8lxc04",createdAt:e,updatedAt:e,publishedAt:e,revisedAt:e,eyecatch:{url:i,height:j,width:a},title:"C博物館",area:"kyoto",category:[k],text:"\u003Cp\u003E設立80年の歴史ある博物館です。\u003Cbr\u003E校外学習から大学の研究まで、広く学問に貢献してきました。\u003Cbr\u003E\u003Cbr\u003E駐車場も無料で、内部には図書館もあるので、1日をゆっくり過ごしたい大人も多く訪れます。\u003C\u002Fp\u003E"},{id:"mvf0k_iptjcf",createdAt:l,updatedAt:m,publishedAt:l,revisedAt:m,eyecatch:{url:i,height:j,width:a},title:"B博物館",area:"tokyo",category:[k],text:"\u003Cp\u003E旧石器時代からの歴史も学べる、由緒ある博物館です。\u003Cbr\u003E\u003Cbr\u003Eスイッチを押すと動く恐竜のレプリカが、小さい子どもたちに人気です。\u003C\u002Fp\u003E"},{id:"2forg38qyrqy",createdAt:n,updatedAt:o,publishedAt:n,revisedAt:o,eyecatch:{url:f,height:g,width:a},title:"A公園",area:"ibaraki",category:[h],text:"\u003Cp\u003E大人でも子どもでも楽しめる公園です。\u003Cbr\u003E\u003Cbr\u003E雨の日は敷地内の売店の中で、名物のカレーライスを食べながら団欒する家族連れで賑わっています。\u003C\u002Fp\u003E"}],totalCount:6,offset:0,limit:10}],fetch:{},mutations:[]}}(640,"2021-04-21T12:39:52.757Z","2021-04-21T12:36:01.117Z","2021-04-21T12:34:14.180Z","2021-04-21T12:32:00.632Z","https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F0dd41c8e4be147079a534c89fc216bfe\u002F2431c69562f34f2f9d209a107f89839c\u002Fpark_default.jpg",427,"park","https:\u002F\u002Fimages.microcms-assets.io\u002Fassets\u002F0dd41c8e4be147079a534c89fc216bfe\u002F38464d5678744ea5af89a342f9e8edb9\u002Fmuseum_default.jpg",480,"museum","2021-04-21T12:22:29.946Z","2021-04-21T12:40:19.720Z","2021-04-21T12:20:51.808Z","2021-04-21T12:40:30.958Z")));